import React, { useState, useEffect, useRef } from 'react';
import { ToastContainer, toast, Slide } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Document, Page, pdfjs } from 'react-pdf';
import {
  Plus,
  Trash2,
  FileText,
  Send,
  Moon,
  Sun,
  ChevronLeft,
  ChevronRight,
  X,
  FileUp,
  Clock,
  MessageSquare,
  Star,
  Edit,
  Menu,
  Eye
} from 'lucide-react';
import './styles/globals.css';
import { Tooltip } from 'react-tooltip';
import 'react-tooltip/dist/react-tooltip.css';

// Configure react-pdf worker
pdfjs.GlobalWorkerOptions.workerSrc = '/pdf.worker.min.js';

const API_BASE_URL = 'http://localhost:8000'; // Assuming FastAPI runs on port 8000

function getToastIcon(type) {
  switch (type) {
    case 'success':
      return '✅';
    case 'error':
      return '❌';
    case 'info':
      return 'ℹ️';
    case 'warning':
      return '⚠️';
    default:
      return '🔔';
  }
}

function TypingBubble() {
  return (
    <div className="chat-bubble chat-bubble-ai typing-bubble">
      <div className="typing-dots">
        <span></span><span></span><span></span>
      </div>
    </div>
  );
}

function ChatItem({ 
  chat, 
  isSelected, 
  isFavorited, 
  onSelect, 
  onRename, 
  onFavorite, 
  onDelete, 
  renamingChatId, 
  newChatName, 
  setNewChatName 
}) {
  const isRenaming = renamingChatId === chat.id;

  return (
    <div 
      className={`
        group relative flex flex-col transition-all duration-200
        ${isSelected 
          ? 'bg-blue-50 dark:bg-blue-900/20 border-blue-200 dark:border-blue-800' 
          : isFavorited
            ? 'bg-amber-50/50 dark:bg-amber-900/10 border-amber-200 dark:border-amber-800/50 hover:bg-amber-50 dark:hover:bg-amber-900/20'
            : 'bg-white dark:bg-gray-800 border-gray-100 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-700/50'
        }
        border rounded-xl shadow-sm hover:shadow-md
        cursor-pointer select-none
        ${isFavorited ? 'ring-1 ring-amber-200 dark:ring-amber-800/50' : ''}
      `}
    >
      <div 
        className="flex items-center justify-between p-3"
        onClick={() => onSelect(chat.id)}
      >
        <div className="flex items-center gap-3 min-w-0 flex-1">
          <div className="flex-shrink-0 relative">
            {isFavorited && (
              <div className="absolute -top-1 -right-1">
                <Star 
                  size={12} 
                  className="text-amber-400 fill-amber-400" 
                />
              </div>
            )}
            <FileText 
              size={18} 
              className={`
                ${isSelected 
                  ? 'text-blue-600 dark:text-blue-400' 
                  : isFavorited
                    ? 'text-amber-600 dark:text-amber-400'
                    : 'text-gray-400 dark:text-gray-500'
                }
                transition-colors duration-200
              `} 
            />
          </div>
          <div className="min-w-0 flex-1">
            {isRenaming ? (
              <input
                type="text"
                value={newChatName}
                onChange={(e) => setNewChatName(e.target.value)}
                onBlur={() => onRename(chat.id, newChatName)}
                onKeyPress={(e) => {
                  if (e.key === 'Enter') {
                    onRename(chat.id, newChatName);
                  }
                }}
                className={`
                  w-full px-2 py-1 text-sm rounded-md 
                  ${isFavorited 
                    ? 'border-amber-200 dark:border-amber-800 focus:ring-amber-500' 
                    : 'border-blue-200 dark:border-blue-800 focus:ring-blue-500'
                  }
                  bg-white dark:bg-gray-800 focus:ring-2 focus:border-transparent
                  transition-all duration-200
                `}
                autoFocus
              />
            ) : (
              <p className={`
                text-sm font-medium truncate
                ${isFavorited 
                  ? 'text-amber-700 dark:text-amber-300' 
                  : 'text-gray-700 dark:text-gray-200'
                }
              `}>
                {chat.name}
              </p>
            )}
          </div>
        </div>

        <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
          <button
            data-tooltip-id="rename-tooltip"
            data-tooltip-content="Rename chat"
            onClick={(e) => {
              e.stopPropagation();
              onRename(chat.id, chat.name);
            }}
            className={`
              p-1.5 rounded-lg transition-colors duration-200
              ${isFavorited
                ? 'hover:bg-amber-100 dark:hover:bg-amber-900/30 text-amber-600 dark:text-amber-400'
                : 'hover:bg-gray-100 dark:hover:bg-gray-700 text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200'
              }
            `}
          >
            <Edit size={14} />
          </button>

          <button
            data-tooltip-id="favorite-tooltip"
            data-tooltip-content={isFavorited ? "Remove from favorites" : "Add to favorites"}
            onClick={(e) => {
              e.stopPropagation();
              onFavorite(chat.id);
            }}
            className={`
              p-1.5 rounded-lg transition-colors duration-200
              ${isFavorited
                ? 'hover:bg-amber-100 dark:hover:bg-amber-900/30'
                : 'hover:bg-gray-100 dark:hover:bg-gray-700'
              }
            `}
          >
            <Star 
              size={14} 
              className={isFavorited 
                ? 'text-amber-400 fill-amber-400' 
                : 'text-gray-400 dark:text-gray-500 hover:text-amber-400 dark:hover:text-amber-400'
              } 
            />
          </button>

          <button
            data-tooltip-id="delete-tooltip"
            data-tooltip-content="Delete chat"
            onClick={(e) => {
              e.stopPropagation();
              onDelete(chat.id);
            }}
            className={`
              p-1.5 rounded-lg transition-colors duration-200
              ${isFavorited
                ? 'hover:bg-red-50 dark:hover:bg-red-900/30 text-amber-600/70 hover:text-red-500 dark:text-amber-400/70 dark:hover:text-red-400'
                : 'hover:bg-red-50 dark:hover:bg-red-900/30 text-gray-400 hover:text-red-500 dark:text-gray-500 dark:hover:text-red-400'
              }
            `}
          >
            <Trash2 size={14} />
          </button>
        </div>
      </div>
    </div>
  );
}

function App() {
  const [chats, setChats] = useState([]);
  const [currentChatId, setCurrentChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPdfModal, setShowPdfModal] = useState(false);
  const [pdfUrlToPreview, setPdfUrlToPreview] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [showTyping, setShowTyping] = useState(false);
  const typingTimeoutRef = useRef(null);

  const messagesEndRef = useRef(null);
  const mainFileInputRef = useRef(null);

  const [favoritedChats, setFavoritedChats] = useState(new Set());
  const [attachedPDFs, setAttachedPDFs] = useState([]);
  const [userLanguage, setUserLanguage] = useState('en');

  // Add a new state for the chat being renamed
  const [renamingChatId, setRenamingChatId] = useState(null);
  const [newChatName, setNewChatName] = useState('');

  const [isSidebarOpen, setIsSidebarOpen] = useState(() => {
    const saved = localStorage.getItem('sidebarOpen');
    return saved !== null ? JSON.parse(saved) : true;
  });

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    setPageNumber(1); // Reset to first page when new PDF loads
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleFileUpload = async (event) => {
    const files = Array.from(event.target.files);
    if (files.length === 0) return;

    // If we have a current chat, append to it; otherwise create new chat
    await uploadFilesToChat(files, currentChatId);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();

    const files = Array.from(e.dataTransfer.files).filter(file => file.type === 'application/pdf');
    if (files.length > 0) {
      const event = { target: { files } };
      handleFileUpload(event);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!question.trim() || !currentChatId) return;

    // Detect user's language
    try {
      const response = await fetch(`${API_BASE_URL}/detect-language/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text: question }),
      });

      if (response.ok) {
        const data = await response.json();
        setUserLanguage(data.language);
      }
    } catch (e) {
      console.error('Language detection failed:', e);
    }

    const userMessage = { role: 'user', content: question };
    setMessages((prevMessages) => [...prevMessages, userMessage]);
    setQuestion('');
    setLoading(true);
    setShowTyping(false);
    if (typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);
    typingTimeoutRef.current = setTimeout(() => setShowTyping(true), 300);

    try {
      const response = await fetch(`${API_BASE_URL}/chat/${currentChatId}/message/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          text: userMessage.content,
          sender: 'user',
          language: userLanguage
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`HTTP error! status: ${response.status} - ${errorData.detail || response.statusText}`);
      }

      const data = await response.json();
      setMessages((prevMessages) => [...prevMessages, { role: 'assistant', content: data.response }]);
      fetchChats();
      toast.success("Answer generated! 💬");

    } catch (e) {
      setMessages((prevMessages) => prevMessages.slice(0, prevMessages.length - 1)); // Remove user message if failed
      toast.error(`Failed to generate answer: ${e.message} ⚠️`);
    } finally {
      setLoading(false);
      setShowTyping(false); // Hide typing animation when response is complete
      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
        typingTimeoutRef.current = null;
      }
    }
  };

  const handleDeleteChat = async (chatId) => {
    if (window.confirm("Are you sure you want to delete this chat? This action cannot be undone.")) {
      setLoading(true);
      try {
        const response = await fetch(`${API_BASE_URL}/chats/${chatId}/`, {
          method: 'DELETE',
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(`HTTP error! status: ${response.status} - ${errorData.detail || response.statusText}`);
        }

        if (currentChatId === chatId) {
          setCurrentChatId(null);
          setMessages([]);
          setAttachedPDFs([]);
        }
        setChats(prevChats => prevChats.filter(chat => chat.id !== chatId));
        toast.success("Chat deleted successfully! 🗑️");
      } catch (e) {
        toast.error(`Failed to delete chat: ${e.message} ⚠️`);
      } finally {
        setLoading(false);
      }
    }
  };

  const handleDeleteAllChats = async () => {
    if (window.confirm("Are you sure you want to delete ALL chats? This action cannot be undone.")) {
      setLoading(true);
      try {
        console.log("Sending delete all chats request...");
        const response = await fetch(`${API_BASE_URL}/chats/all/`, {
          method: 'DELETE',
        });

        console.log("Delete all chats response status:", response.status);

        if (!response.ok) {
          const errorData = await response.json();
          console.error("Delete all chats error:", errorData);
          throw new Error(`HTTP error! status: ${response.status} - ${errorData.detail || response.statusText}`);
        }

        const result = await response.json();
        console.log("Delete all chats success:", result);

        setCurrentChatId(null);
        setChats([]);
        setMessages([]);
        setAttachedPDFs([]);
        toast.success("All chats deleted! 🗑️");

        // Refresh the chat list to ensure UI is updated
        await fetchChats();
      } catch (e) {
        console.error("Delete all chats failed:", e);
        toast.error(`Failed to delete all chats: ${e.message} ⚠️`);
      } finally {
        setLoading(false);
      }
    }
  };

  const handlePdfPreview = (pdfPath) => {
    if (!pdfPath) {
      toast.error("PDF path is not available.");
      return;
    }
    const pdfUrl = `${API_BASE_URL}/pdfs/${pdfPath}`; // Ensure the URL is correctly constructed
    setPdfUrlToPreview(pdfUrl);
    setShowPdfModal(true);
  };

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    document.documentElement.classList.toggle('dark');
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
    localStorage.setItem('sidebarOpen', JSON.stringify(!isSidebarOpen));
  };

  // Keyboard shortcut to toggle sidebar (Ctrl/Cmd + B)
  useEffect(() => {
    const handleKeyDown = (event) => {
      if ((event.ctrlKey || event.metaKey) && event.key === 'b') {
        event.preventDefault();
        toggleSidebar();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isSidebarOpen]);

  const toggleFavorite = (chatId) => {
    setFavoritedChats(prev => {
      const newFavorited = new Set(prev);
      if (newFavorited.has(chatId)) {
        newFavorited.delete(chatId);
      } else {
        newFavorited.add(chatId);
      }
      return newFavorited;
    });
  };

  const uploadFilesToChat = async (files, targetChatId = null) => {
    console.log('uploadFilesToChat called with:', files.length, 'files, targetChatId:', targetChatId);
    if (files.length === 0) return;

    setLoading(true);
    toast.info("Uploading and processing PDFs...");

    try {
      const uploadPromises = files.map(async (file) => {
        console.log('Uploading file:', file.name);
        const formData = new FormData();
        formData.append('file', file);

        // Add chat_id to form data if we're appending to an existing chat
        if (targetChatId) {
          formData.append('chat_id', targetChatId);
          console.log('Appending to existing chat:', targetChatId);
        } else {
          console.log('Creating new chat');
        }

        const response = await fetch(`${API_BASE_URL}/upload-pdf/`, {
          method: 'POST',
          body: formData,
        });

        console.log('Upload response status:', response.status);
        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(`HTTP error! status: ${response.status} - ${errorData.detail || response.statusText}`);
        }

        const result = await response.json();
        console.log('Upload result:', result);

        return result;
      });

      const results = await Promise.all(uploadPromises);
      console.log('All upload results:', results);

      // Update the attached PDFs for the current chat
      if (targetChatId && currentChatId === targetChatId) {
        console.log('Updating existing chat with new files');
        setAttachedPDFs(prev => {
          const newFiles = [...prev, ...results.map(file => ({
            path: file.pdf_path,
            name: file.chat_name,
            language: file.language
          }))];
          console.log('New attachedPDFs:', newFiles);
          return newFiles;
        });
      }

      // Set the first chat as current if none is selected and we're not appending
      if (!currentChatId && !targetChatId && results.length > 0) {
        console.log('Setting new chat as current:', results[0].chat_id);
        setCurrentChatId(results[0].chat_id);
        const newFiles = results.map(file => ({
          path: file.pdf_path,
          name: file.chat_name,
          language: file.language
        }));
        console.log('Setting attachedPDFs for new chat:', newFiles);
        setAttachedPDFs(newFiles);
      }

      fetchChats();
      const action = targetChatId ? 'appended to chat' : 'uploaded and processed';
      toast.success(`${files.length} PDF${files.length > 1 ? 's' : ''} ${action} successfully! ✅`);
    } catch (e) {
      console.error('Upload error:', e);
      toast.error(`PDF upload failed: ${e.message} ❌`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchChats();
  }, []);

  useEffect(() => {
    if (currentChatId) {
      fetchChatMessages(currentChatId);
    } else {
      setMessages([]);
      setAttachedPDFs([]);
    }
  }, [currentChatId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    // Check for saved theme preference
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'dark') {
      setIsDarkMode(true);
      document.documentElement.classList.add('dark');
    }
  }, []);

  useEffect(() => {
    return () => {
      if (typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);
    };
  }, []);

  const fetchChats = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/chats/`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      if (data && Array.isArray(data.chats)) {
        setChats(data.chats);
      } else {
        setChats([]);
        console.warn('Received invalid chat data format:', data);
      }
    } catch (e) {
      setChats([]); // Clear chats on error
    }
  };

  const fetchChatMessages = async (chatId) => {
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/chats/${chatId}/`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      if (data && Array.isArray(data.messages)) {
        setMessages(data.messages);
        // Load associated PDFs
        if (data.pdf_paths) {
          setAttachedPDFs(data.pdf_paths.map((path, index) => ({
            path: path,
            name: data.pdf_names?.[index] || path.split('/').pop(),
            language: data.language || 'en'
          })));
        }
      } else {
        setMessages([]);
        setAttachedPDFs([]);
        console.warn('Received invalid chat message data format:', data);
      }
    } catch (e) {
      setMessages([]); // Clear messages on error
      setAttachedPDFs([]); // Clear PDFs on error
      toast.error(`Failed to load chat: ${e.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`flex h-screen bg-background-light dark:bg-background-dark text-text-primary-light dark:text-text-primary-dark transition-colors duration-200 ${!isSidebarOpen ? 'sidebar-closed' : ''}`}>
      <ToastContainer
        position="bottom-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme={isDarkMode ? "dark" : "light"}
        limit={3}
        transition={Slide}
        icon={({ type }) => getToastIcon(type)}
      />

      {/* Sidebar */}
      <aside className={`
        sidebar w-80 p-6 flex flex-col shadow-lg z-40 
        bg-white dark:bg-gray-800 border-r border-gray-200 dark:border-gray-700
        transition-all duration-300 ease-in-out
        ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'}
        fixed md:fixed md:top-0 md:left-0 md:h-full
        ${isSidebarOpen ? 'left-0' : '-left-full md:left-0'}
      `}>
        <div className="flex justify-between items-center mb-8">
          <h3 className="text-3xl font-extrabold tracking-wide gradient-text">Inquiero</h3>
          <button
            onClick={toggleSidebar}
            className="btn-icon btn-icon-primary hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors duration-200"
            data-tooltip-id="sidebar-tooltip"
            data-tooltip-content={`${isSidebarOpen ? "Close sidebar" : "Open sidebar"} (Ctrl+B)`}
          >
            <Menu size={20} />
          </button>
        </div>

        <div className="space-y-3 mb-6">
          <button 
            className="btn btn-primary w-full flex items-center justify-center gap-2 hover:shadow-lg transition-all duration-200"
            onClick={() => {
              setCurrentChatId(null);
              setMessages([]);
              setAttachedPDFs([]); // Clear attached PDFs for new chat
            }}
            data-tooltip-id="new-chat-tooltip"
            data-tooltip-content="Start a new chat"
          >
            <Plus size={20} /> New Chat
          </button>

          <button 
            className="btn btn-secondary w-full flex items-center justify-center gap-2 hover:shadow-lg transition-all duration-200"
            onClick={handleDeleteAllChats}
            data-tooltip-id="delete-all-tooltip"
            data-tooltip-content="Delete all chats"
          >
            <Trash2 size={20} /> Delete All
          </button>
        </div>

        <div className="flex-1 overflow-y-auto pr-2 -mr-2 space-y-2">
          {chats.length === 0 ? (
            <div className="text-center py-8">
              <FileText size={40} className="mx-auto mb-4 text-gray-400" />
              <p className="text-gray-500 dark:text-gray-400 text-sm">No chats yet. Upload a PDF to start!</p>
            </div>
          ) : (
            [...chats]
              .sort((a, b) => {
                // First sort by favorite status
                const aFavorited = favoritedChats.has(a.id);
                const bFavorited = favoritedChats.has(b.id);
                if (aFavorited && !bFavorited) return -1;
                if (!aFavorited && bFavorited) return 1;

                // Then sort by creation date (newest first)
                const aDate = new Date(a.created_at || 0);
                const bDate = new Date(b.created_at || 0);
                return bDate - aDate;
              })
              .map((chat) => (
                <ChatItem
                  key={chat.id}
                  chat={chat}
                  isSelected={currentChatId === chat.id}
                  isFavorited={favoritedChats.has(chat.id)}
                  onSelect={setCurrentChatId}
                  onRename={(id, name) => {
                    setRenamingChatId(id);
                    setNewChatName(name);
                  }}
                  onFavorite={toggleFavorite}
                  onDelete={handleDeleteChat}
                  renamingChatId={renamingChatId}
                  newChatName={newChatName}
                  setNewChatName={setNewChatName}
                />
              ))
          )}
        </div>

        {/* Tooltips */}
        <Tooltip id="rename-tooltip" place="top" />
        <Tooltip id="favorite-tooltip" place="top" />
        <Tooltip id="delete-tooltip" place="top" />
        <Tooltip id="sidebar-tooltip" place="bottom" />
        <Tooltip id="new-chat-tooltip" place="bottom" />
        <Tooltip id="delete-all-tooltip" place="bottom" />
        <Tooltip id="upload-tooltip" place="bottom" />
        <Tooltip id="theme-tooltip" place="bottom" />
        <Tooltip id="preview-tooltip" place="top" />
        <Tooltip id="remove-tooltip" place="top" />
      </aside>

      {/* Sidebar Overlay for mobile */}
      {isSidebarOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-30 md:hidden"
          onClick={toggleSidebar}
        />
      )}

      {/* Main Chat Area */}
      <main className={`
        flex-1 flex flex-col transition-all duration-300 ease-in-out
        ${isSidebarOpen ? 'md:ml-80' : 'ml-0'}
        ${isSidebarOpen ? 'md:w-auto' : 'w-full'}
        ${isSidebarOpen ? 'md:max-w-none' : 'max-w-full'}
        ${isSidebarOpen ? 'md:relative' : 'md:relative'}
      `}>
        {/* Floating Sidebar Toggle Button - Only visible when sidebar is closed */}
        {!isSidebarOpen && (
          <div className="absolute top-4 left-4 z-50">
            <button
              onClick={toggleSidebar}
              className="btn-icon btn-icon-primary hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors duration-200 shadow-lg"
              data-tooltip-id="sidebar-tooltip"
              data-tooltip-content="Open sidebar (Ctrl+B)"
            >
              <Menu size={20} />
            </button>
          </div>
        )}

        {/* Dark Mode Toggle Button - Top Right */}
        <div className="absolute top-4 right-4 z-50">
          <button
            onClick={toggleDarkMode}
            className="btn-icon btn-icon-primary hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors duration-200 shadow-lg"
            data-tooltip-id="theme-tooltip"
            data-tooltip-content={isDarkMode ? "Switch to light mode" : "Switch to dark mode"}
          >
            {isDarkMode ? <Sun size={20} /> : <Moon size={20} />}
          </button>
        </div>

        <div className={`flex-1 overflow-y-auto space-y-4 transition-all duration-300 ${isSidebarOpen ? 'md:max-w-none p-6' : 'max-w-full p-6 md:p-8 lg:p-12'}`}>
          {currentChatId && (
            <div className={`mb-6 transition-all duration-300 ${isSidebarOpen ? 'md:max-w-4xl md:mx-auto' : 'w-full'}`}>
              <h3 className="text-lg font-semibold mb-2 flex items-center gap-2">
                <FileText size={20} className="text-accent-blue" />
                Attached PDFs
              </h3>
              {attachedPDFs.length === 0 ? (
                <div
                  className="flex flex-col items-center justify-center min-h-[220px] border-2 border-dashed border-gray-300 dark:border-gray-700 rounded-xl bg-gray-50 dark:bg-gray-900/40 p-8 transition-colors duration-200"
                  onDragOver={handleDragOver}
                  onDrop={handleDrop}
                  style={{ cursor: 'pointer' }}
                  onClick={() => { if (mainFileInputRef.current) mainFileInputRef.current.click(); }}
                >
                  <FileUp size={40} className="mb-4 text-blue-400 dark:text-blue-500" />
                  <p className="text-base font-medium text-gray-700 dark:text-gray-200 mb-2">Drag & drop PDF here</p>
                  <button
                    type="button"
                    className="btn btn-primary px-6 py-2 mt-2 rounded-lg text-base font-semibold shadow-sm"
                    onClick={e => { e.stopPropagation(); if (mainFileInputRef.current) mainFileInputRef.current.click(); }}
                  >
                    Upload PDF
                  </button>
                </div>
              ) : (
                <div className="space-y-3">
                  <div className={`grid gap-3 ${isSidebarOpen ? 'md:grid-cols-1' : 'grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4'}`}>
                    {attachedPDFs.map((file, idx) => (
                      <div 
                        key={file.path || idx} 
                        className="group relative flex items-center justify-between p-3 bg-gray-50 dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700"
                      >
                        <div className="flex items-center gap-3 min-w-0 flex-1">
                          <FileText size={20} className="text-accent-blue dark:text-accent-purple flex-shrink-0" />
                          <div className="min-w-0 flex-1">
                            <span className="text-sm font-medium truncate block">{file.name}</span>
                          </div>
                        </div>
                        <button 
                          onClick={() => handlePdfPreview(file.path)}
                          className="btn-icon btn-icon-primary p-1.5 opacity-0 group-hover:opacity-100"
                          data-tooltip-id="preview-tooltip"
                          data-tooltip-content="Preview PDF"
                        >
                          <Eye size={14} />
                        </button>
                      </div>
                    ))}
                  </div>
                  <button
                    onClick={() => { if (mainFileInputRef.current) mainFileInputRef.current.click(); }}
                    className="btn btn-secondary text-sm flex items-center gap-2"
                  >
                    <FileUp size={16} /> Add More
                  </button>
                </div>
              )}
            </div>
          )}
          {currentChatId ? (
            messages.length > 0 ? (
              <div className={`space-y-4 transition-all duration-300 ${isSidebarOpen ? 'md:max-w-4xl md:mx-auto' : 'w-full'}`}>
                {messages.map((message, index) => (
                  <div
                    key={index}
                    className={`chat-bubble ${
                      message.role === 'user' ? 'chat-bubble-user' : 'chat-bubble-ai'
                    }`}
                  >
                    <div className="flex items-start">
                      {message.role === 'user' ? (
                        <MessageSquare size={20} className="mr-2 text-accent-blue" />
                      ) : (
                        <Clock size={20} className="mr-2 text-accent-purple" />
                      )}
                      <div>{message.content}</div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="flex items-center justify-center h-full text-text-secondary">
                <p>Start by asking a question about your PDF!</p>
              </div>
            )
          ) : (
            <div className="flex items-center justify-center h-full">
              <div className="text-center">
                <h2 className="text-2xl font-bold mb-4">Welcome to Inquiero! 👋</h2>
                <p className="text-text-secondary mb-8">Upload a PDF to start asking questions</p>
                <label 
                  className="file-upload-btn"
                  onDragOver={handleDragOver}
                  onDrop={handleDrop}
                >
                  <FileUp size={20} className="mr-2" /> Upload PDF
                  <input
                    type="file"
                    accept=".pdf"
                    multiple
                    onChange={handleFileUpload}
                  />
                </label>
              </div>
            </div>
          )}
          {showTyping && <TypingBubble />}
          <div ref={messagesEndRef} />
        </div>

        {/* Persistent bottom panel for attached PDFs */}
        {currentChatId && (
          <div className="fixed bottom-20 left-0 right-0 z-30 flex justify-center">
            <div className="bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-700 rounded-xl shadow-lg px-4 py-3 flex items-center gap-4 max-w-2xl w-full mx-4">
              <div className="flex-1">
                <div className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  Attached Files ({attachedPDFs.length})
                </div>
                <div className="flex flex-wrap gap-2">
                  {attachedPDFs.length === 0 ? (
                    <span className="text-gray-500 dark:text-gray-400 text-sm">No PDFs attached yet</span>
                  ) : (
                    attachedPDFs.map((file, idx) => (
                      <span key={file.path || idx} className="inline-flex items-center gap-1 px-2 py-1 bg-blue-50 dark:bg-blue-900/30 rounded text-xs font-medium text-blue-700 dark:text-blue-200 border border-blue-200 dark:border-blue-700">
                        <FileText size={12} />
                        {file.name}
                      </span>
                    ))
                  )}
                </div>
              </div>
              <button
                className="btn btn-secondary btn-sm flex items-center gap-2 whitespace-nowrap"
                onClick={() => {
                  if (mainFileInputRef.current) {
                    mainFileInputRef.current.click();
                  }
                }}
              >
                <FileUp size={16} /> Upload More
              </button>
            </div>
          </div>
        )}

        <form onSubmit={handleSendMessage} className={`border-t border-gray-200 dark:border-gray-700 transition-all duration-300 ${isSidebarOpen ? 'md:max-w-none p-4' : 'max-w-full p-4 md:p-6 lg:p-8'}`}>
          <div className={`flex space-x-4 transition-all duration-300 ${isSidebarOpen ? 'md:max-w-4xl md:mx-auto' : 'w-full'}`}>
            <input
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Ask a question about your PDF..."
              className="input-field flex-1"
              disabled={loading || !currentChatId}
            />
            <button
              type="submit"
              disabled={loading || !question.trim() || !currentChatId}
              className="btn btn-primary flex-shrink-0"
            >
              {loading ? <Clock size={20} className="animate-spin" /> : <Send size={20} />}
            </button>
          </div>
        </form>

        {/* Hidden file input for main content area */}
        <input
          type="file"
          ref={mainFileInputRef}
          accept=".pdf"
          multiple
          onChange={handleFileUpload}
          className="hidden"
        />
      </main>

      {/* PDF Preview Modal */}
      {showPdfModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white dark:bg-gray-800 rounded-xl p-4 max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-bold">PDF Preview</h3>
              <button
                onClick={() => setShowPdfModal(false)}
                className="btn-icon btn-icon-secondary"
              >
                <X size={24} />
              </button>
            </div>
            <Document
              file={pdfUrlToPreview}
              onLoadSuccess={onDocumentLoadSuccess}
              className="flex justify-center"
            >
              <Page
                pageNumber={pageNumber}
                renderTextLayer={false}
                renderAnnotationLayer={false}
                className="shadow-soft"
              />
            </Document>
            <div className="flex justify-center mt-4 space-x-4">
              <button
                onClick={() => setPageNumber(Math.max(1, pageNumber - 1))}
                disabled={pageNumber <= 1}
                className="btn btn-icon btn-icon-secondary"
              >
                <ChevronLeft size={20} />
              </button>
              <span className="py-2">
                Page {pageNumber} of {numPages}
              </span>
              <button
                onClick={() => setPageNumber(Math.min(numPages, pageNumber + 1))}
                disabled={pageNumber >= numPages}
                className="btn btn-icon btn-icon-secondary"
              >
                <ChevronRight size={20} />
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
