import React, { useState, useEffect, useRef } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Document, Page, pdfjs } from 'react-pdf';
import './styles/globals.css';

// Configure react-pdf worker
pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.js`;

const API_BASE_URL = 'http://localhost:8000'; // Assuming FastAPI runs on port 8000

function App() {
  const [chats, setChats] = useState([]);
  const [currentChatId, setCurrentChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showPdfModal, setShowPdfModal] = useState(false);
  const [pdfUrlToPreview, setPdfUrlToPreview] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [isDarkMode, setIsDarkMode] = useState(false);

  const messagesEndRef = useRef(null);

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    setPageNumber(1); // Reset to first page when new PDF loads
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    fetchChats();
  }, []);

  useEffect(() => {
    if (currentChatId) {
      fetchChatMessages(currentChatId);
    } else {
      setMessages([]);
    }
  }, [currentChatId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    // Check for saved theme preference
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'dark') {
      setIsDarkMode(true);
      document.documentElement.classList.add('dark');
    }
  }, []);

  const fetchChats = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/chats/`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setChats(data.chats);
    } catch (e) {
      setError(`Failed to fetch chats: ${e.message}`);
      toast.error(`Failed to fetch chats: ${e.message}`);
    }
  };

  const fetchChatMessages = async (chatId) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_BASE_URL}/chats/${chatId}/`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setMessages(data.messages);
    } catch (e) {
      setError(`Failed to fetch messages: ${e.message}`);
      setMessages([]); // Clear messages on error
      toast.error(`Failed to load chat: ${e.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);
    setError(null);
    toast.info("Uploading and processing PDF...");
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch(`${API_BASE_URL}/upload-pdf/`, {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`HTTP error! status: ${response.status} - ${errorData.detail || response.statusText}`);
      }

      const data = await response.json();
      setCurrentChatId(data.chat_id);
      fetchChats(); 
      toast.success("PDF uploaded and processed successfully! ‚úÖ");

    } catch (e) {
      setError(`Failed to upload PDF: ${e.message}`);
      toast.error(`PDF upload failed: ${e.message} ‚ùå`);
    } finally {
      setLoading(false);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!question.trim() || !currentChatId) return;

    const userMessage = { role: 'user', content: question };
    setMessages((prevMessages) => [...prevMessages, userMessage]);
    setQuestion('');
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`${API_BASE_URL}/chat/${currentChatId}/message/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ role: 'user', content: userMessage.content }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`HTTP error! status: ${response.status} - ${errorData.detail || response.statusText}`);
      }

      const data = await response.json();
      setMessages((prevMessages) => [...prevMessages, { role: 'assistant', content: data.answer }]);
      fetchChats();
      toast.success("Answer generated! üí¨");

    } catch (e) {
      setError(`Failed to send message: ${e.message}`);
      setMessages((prevMessages) => prevMessages.slice(0, prevMessages.length - 1)); // Remove user message if failed
      toast.error(`Failed to generate answer: ${e.message} ‚ö†Ô∏è`);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteChat = async (chatId) => {
    if (window.confirm("Are you sure you want to delete this chat?")) {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(`${API_BASE_URL}/chats/${chatId}/`, {
          method: 'DELETE',
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(`HTTP error! status: ${response.status} - ${errorData.detail || response.statusText}`);
        }

        if (currentChatId === chatId) {
          setCurrentChatId(null);
        }
        fetchChats();
        toast.success("Chat deleted! üóëÔ∏è");
      } catch (e) {
        setError(`Failed to delete chat: ${e.message}`);
        toast.error(`Failed to delete chat: ${e.message} ‚ö†Ô∏è`);
      } finally {
        setLoading(false);
      }
    }
  };

  const handleDeleteAllChats = async () => {
    if (window.confirm("Are you sure you want to delete ALL chats? This action cannot be undone.")) {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(`${API_BASE_URL}/chats/all/`, {
          method: 'DELETE',
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(`HTTP error! status: ${response.status} - ${errorData.detail || response.statusText}`);
        }

        setCurrentChatId(null);
        setChats([]);
        setMessages([]);
        setError(null);
        toast.success("All chats deleted! üóëÔ∏è");
        fetchChats();
      } catch (e) {
        setError(`Failed to delete all chats: ${e.message}`);
        toast.error(`Failed to delete all chats: ${e.message} ‚ö†Ô∏è`);
      } finally {
        setLoading(false);
      }
    }
  };

  const handlePdfPreview = (pdfPath) => {
    // Assuming pdfPath is a relative path to the backend's data directory
    // You might need to adjust this URL based on your FastAPI serving static files or an endpoint
    const pdfUrl = `${API_BASE_URL}/static/${pdfPath.split('/').pop()}`; // Adjust as per your backend serving strategy
    setPdfUrlToPreview(pdfUrl);
    setShowPdfModal(true);
  };

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    if (!isDarkMode) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  };

  return (
    <div className="flex h-screen bg-background-light dark:bg-background-dark text-text-primary-light dark:text-text-primary-dark transition-colors duration-200">
      <ToastContainer position="bottom-right" autoClose={3000} hideProgressBar newestOnTop closeOnClick rtl={false} pauseOnFocusLoss draggable pauseOnHover theme={isDarkMode ? "dark" : "light"} />

      {/* Sidebar */}
      <aside className="sidebar w-72 p-6 flex flex-col shadow-soft z-10">
        <div className="flex justify-between items-center mb-8">
          <h3 className="text-3xl font-extrabold tracking-wide">Inquiero</h3>
          <button
            onClick={toggleDarkMode}
            className="p-2 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors"
          >
            {isDarkMode ? 'üåû' : 'üåô'}
          </button>
        </div>
        
        <button 
          className="btn btn-primary mb-4 w-full"
          onClick={() => {
            setCurrentChatId(null);
            setMessages([]);
          }}
        >
          <span className="text-xl mr-2">‚ûï</span> New Chat
        </button>
        
        <button 
          className="btn btn-secondary mb-8 w-full"
          onClick={handleDeleteAllChats}
        >
          <span className="text-xl mr-2">üóëÔ∏è</span> Delete All
        </button>
        
        <div className="overflow-y-auto flex-grow pr-2 -mr-2">
          {chats.length === 0 ? (
            <p className="text-text-secondary text-center text-sm">No chats yet. Upload a PDF to start!</p>
          ) : (
            chats.map((chat) => (
              <div 
                key={chat.id} 
                className={`group flex flex-col rounded-xl p-4 mb-3 shadow-soft hover:shadow-md transition-all duration-200 cursor-pointer ${
                  currentChatId === chat.id 
                    ? 'bg-accent-blue bg-opacity-10 dark:bg-accent-blue dark:bg-opacity-20' 
                    : 'bg-white dark:bg-gray-800 hover:bg-gray-50 dark:hover:bg-gray-700'
                }`}
              >
                <div className="flex items-center justify-between" onClick={() => setCurrentChatId(chat.id)}>
                  <span className="font-medium truncate">{chat.name}</span>
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDeleteChat(chat.id);
                    }}
                    className="opacity-0 group-hover:opacity-100 text-red-500 hover:text-red-600 transition-opacity"
                  >
                    üóëÔ∏è
                  </button>
                </div>
              </div>
            ))
          )}
        </div>

        <div className="mt-4">
          <label className="btn btn-primary w-full cursor-pointer">
            <span className="text-xl mr-2">üìÑ</span> Upload PDF
            <input
              type="file"
              accept=".pdf"
              onChange={handleFileUpload}
              className="hidden"
            />
          </label>
        </div>
      </aside>

      {/* Main Chat Area */}
      <main className="flex-1 flex flex-col">
        {currentChatId ? (
          <>
            <div className="flex-1 overflow-y-auto p-6 space-y-4">
              {messages.map((message, index) => (
                <div
                  key={index}
                  className={`chat-bubble ${
                    message.role === 'user' ? 'chat-bubble-user' : 'chat-bubble-ai'
                  }`}
                >
                  {message.content}
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            <form onSubmit={handleSendMessage} className="p-4 border-t border-gray-200 dark:border-gray-700">
              <div className="flex space-x-4">
                <input
                  type="text"
                  value={question}
                  onChange={(e) => setQuestion(e.target.value)}
                  placeholder="Ask a question about your PDF..."
                  className="input-field"
                  disabled={loading}
                />
                <button
                  type="submit"
                  disabled={loading || !question.trim()}
                  className="btn btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? '‚åõ' : '‚û§'}
                </button>
              </div>
            </form>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center">
            <div className="text-center">
              <h2 className="text-2xl font-bold mb-4">Welcome to Inquiero! üëã</h2>
              <p className="text-text-secondary mb-8">Upload a PDF to start asking questions</p>
              <label className="btn btn-primary cursor-pointer">
                <span className="text-xl mr-2">üìÑ</span> Upload PDF
                <input
                  type="file"
                  accept=".pdf"
                  onChange={handleFileUpload}
                  className="hidden"
                />
              </label>
            </div>
          </div>
        )}
      </main>

      {/* PDF Preview Modal */}
      {showPdfModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white dark:bg-gray-800 rounded-xl p-4 max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-bold">PDF Preview</h3>
              <button
                onClick={() => setShowPdfModal(false)}
                className="text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200"
              >
                ‚úï
              </button>
            </div>
            <Document
              file={pdfUrlToPreview}
              onLoadSuccess={onDocumentLoadSuccess}
              className="flex justify-center"
            >
              <Page
                pageNumber={pageNumber}
                renderTextLayer={false}
                renderAnnotationLayer={false}
                className="shadow-soft"
              />
            </Document>
            <div className="flex justify-center mt-4 space-x-4">
              <button
                onClick={() => setPageNumber(Math.max(1, pageNumber - 1))}
                disabled={pageNumber <= 1}
                className="btn btn-secondary disabled:opacity-50"
              >
                Previous
              </button>
              <span className="py-2">
                Page {pageNumber} of {numPages}
              </span>
              <button
                onClick={() => setPageNumber(Math.min(numPages, pageNumber + 1))}
                disabled={pageNumber >= numPages}
                className="btn btn-secondary disabled:opacity-50"
              >
                Next
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
