import React, { useState, useEffect, useRef } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Document, Page, pdfjs } from 'react-pdf';

// Configure react-pdf worker
pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.js`;

const API_BASE_URL = 'http://localhost:8000'; // Assuming FastAPI runs on port 8000

function App() {
  const [chats, setChats] = useState([]);
  const [currentChatId, setCurrentChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showPdfModal, setShowPdfModal] = useState(false);
  const [pdfUrlToPreview, setPdfUrlToPreview] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);

  const messagesEndRef = useRef(null);

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    setPageNumber(1); // Reset to first page when new PDF loads
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    fetchChats();
  }, []);

  useEffect(() => {
    if (currentChatId) {
      fetchChatMessages(currentChatId);
    } else {
      setMessages([]);
    }
  }, [currentChatId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const fetchChats = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/chats/`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setChats(data.chats);
    } catch (e) {
      setError(`Failed to fetch chats: ${e.message}`);
      toast.error(`Failed to fetch chats: ${e.message}`);
    }
  };

  const fetchChatMessages = async (chatId) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_BASE_URL}/chats/${chatId}/`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setMessages(data.messages);
    } catch (e) {
      setError(`Failed to fetch messages: ${e.message}`);
      setMessages([]); // Clear messages on error
      toast.error(`Failed to load chat: ${e.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);
    setError(null);
    toast.info("Uploading and processing PDF...");
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch(`${API_BASE_URL}/upload-pdf/`, {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`HTTP error! status: ${response.status} - ${errorData.detail || response.statusText}`);
      }

      const data = await response.json();
      setCurrentChatId(data.chat_id);
      fetchChats(); 
      toast.success("PDF uploaded and processed successfully! ✅");

    } catch (e) {
      setError(`Failed to upload PDF: ${e.message}`);
      toast.error(`PDF upload failed: ${e.message} ❌`);
    } finally {
      setLoading(false);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!question.trim() || !currentChatId) return;

    const userMessage = { role: 'user', content: question };
    setMessages((prevMessages) => [...prevMessages, userMessage]);
    setQuestion('');
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`${API_BASE_URL}/chat/${currentChatId}/message/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ role: 'user', content: userMessage.content }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`HTTP error! status: ${response.status} - ${errorData.detail || response.statusText}`);
      }

      const data = await response.json();
      setMessages((prevMessages) => [...prevMessages, { role: 'assistant', content: data.answer }]);
      fetchChats();
      toast.success("Answer generated! 💬");

    } catch (e) {
      setError(`Failed to send message: ${e.message}`);
      setMessages((prevMessages) => prevMessages.slice(0, prevMessages.length - 1)); // Remove user message if failed
      toast.error(`Failed to generate answer: ${e.message} ⚠️`);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteChat = async (chatId) => {
    if (window.confirm("Are you sure you want to delete this chat?")) {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(`${API_BASE_URL}/chats/${chatId}/`, {
          method: 'DELETE',
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(`HTTP error! status: ${response.status} - ${errorData.detail || response.statusText}`);
        }

        if (currentChatId === chatId) {
          setCurrentChatId(null);
        }
        fetchChats();
        toast.success("Chat deleted! 🗑️");
      } catch (e) {
        setError(`Failed to delete chat: ${e.message}`);
        toast.error(`Failed to delete chat: ${e.message} ⚠️`);
      } finally {
        setLoading(false);
      }
    }
  };

  const handleDeleteAllChats = async () => {
    if (window.confirm("Are you sure you want to delete ALL chats? This action cannot be undone.")) {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(`${API_BASE_URL}/chats/all/`, {
          method: 'DELETE',
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(`HTTP error! status: ${response.status} - ${errorData.detail || response.statusText}`);
        }

        setCurrentChatId(null);
        setChats([]);
        setMessages([]);
        setError(null);
        toast.success("All chats deleted! 🗑️");
        fetchChats();
      } catch (e) {
        setError(`Failed to delete all chats: ${e.message}`);
        toast.error(`Failed to delete all chats: ${e.message} ⚠️`);
      } finally {
        setLoading(false);
      }
    }
  };

  const handlePdfPreview = (pdfPath) => {
    // Assuming pdfPath is a relative path to the backend's data directory
    // You might need to adjust this URL based on your FastAPI serving static files or an endpoint
    const pdfUrl = `${API_BASE_URL}/static/${pdfPath.split('/').pop()}`; // Adjust as per your backend serving strategy
    setPdfUrlToPreview(pdfUrl);
    setShowPdfModal(true);
  };

  return (
    <div className="flex h-screen bg-gray-900 text-gray-100 font-sans">
      <ToastContainer position="bottom-right" autoClose={3000} hideProgressBar newestOnTop closeOnClick rtl={false} pauseOnFocusLoss draggable pauseOnHover theme="dark" />

      {/* Sidebar */}
      <aside className="w-72 bg-gray-800 p-6 flex flex-col shadow-2xl z-10">
        <h3 className="text-3xl font-extrabold mb-8 text-white tracking-wide">Inquiero</h3>
        
        <button 
          className="flex items-center justify-center bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-xl mb-4 w-full transition duration-300 ease-in-out transform hover:scale-105 shadow-lg"
          onClick={() => {
            setCurrentChatId(null);
            setMessages([]); // Clear messages when starting a new chat
          }}
        >
          <span className="text-xl mr-2">➕</span> New Chat
        </button>
        <button 
          className="flex items-center justify-center bg-red-600 hover:bg-red-700 text-white font-bold py-3 px-6 rounded-xl mb-8 w-full transition duration-300 ease-in-out transform hover:scale-105 shadow-lg"
          onClick={handleDeleteAllChats}
        >
          <span className="text-xl mr-2">🗑️</span> Delete All
        </button>
        
        <div className="overflow-y-auto flex-grow pr-2 -mr-2">
          {chats.length === 0 ? (
            <p className="text-gray-400 text-center text-sm">No chats yet. Upload a PDF to start!</p>
          ) : (
            chats.map((chat) => (
              <div key={chat.id} className="group flex flex-col bg-gray-700 rounded-xl p-4 mb-3 shadow-md hover:bg-gray-600 transition duration-200 ease-in-out transform hover:scale-[1.02] cursor-pointer">
                <div className="flex items-center justify-between" onClick={() => setCurrentChatId(chat.id)}>
                  <h4 className={`text-lg font-semibold truncate ${currentChatId === chat.id ? 'text-blue-400' : 'text-gray-100'}`}>
                    📄 {chat.name}
                  </h4>
                  <button 
                    className="ml-4 text-gray-400 hover:text-red-400 transition duration-200 ease-in-out text-xl opacity-0 group-hover:opacity-100"
                    onClick={(e) => {
                      e.stopPropagation(); // Prevent chat selection when deleting
                      handleDeleteChat(chat.id);
                    }}
                  >
                    🗑️
                  </button>
                </div>
                {chat.pdf_path && (
                  <div 
                    className="text-sm text-gray-400 mt-2 hover:underline cursor-pointer flex items-center"
                    onClick={() => handlePdfPreview(chat.pdf_path)}
                  >
                    <span className="mr-1 text-base">📎</span> {chat.pdf_path.split('/').pop()}
                  </div>
                )}
              </div>
            ))
          )}
        </div>
      </aside>

      {/* Main Chat Area */}
      <main className="flex-1 flex flex-col bg-gray-900 p-6">
        {/* PDF Upload Area */}
        {!currentChatId && (
          <div className="border-2 border-dashed border-blue-500 rounded-xl p-8 text-center bg-gray-800 mb-6 cursor-pointer hover:border-blue-400 transition duration-300 ease-in-out relative group">
            <h4 className="text-2xl font-bold text-blue-300 mb-3">📄 Drag and drop or click to upload your PDF</h4>
            <p className="text-gray-400 text-base">Limit 200MB per file • PDF</p>
            <input type="file" accept=".pdf" onChange={handleFileUpload} className="hidden" id="file-upload" />
            <label htmlFor="file-upload" className="absolute inset-0 cursor-pointer flex items-center justify-center text-5xl text-blue-400 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></label>
          </div>
        )}

        {/* Loading and Error Messages */}
        {loading && <div className="text-center text-blue-400 text-lg mb-4 animate-pulse">Processing...</div>}
        {error && <div className="text-center text-red-400 text-lg mb-4 animate-bounce">Error: {error}</div>}

        {/* Chat Messages Container */}
        <div className="flex-1 bg-gray-800 rounded-xl p-4 overflow-y-auto shadow-inner mb-6 flex flex-col space-y-4">
          {currentChatId ? (
            messages.length === 0 && !loading && !error ? (
              <div className="flex items-center justify-center h-full text-gray-500 text-center text-lg p-4 animate-fade-in">
                <p>Start by asking a question about your PDF!</p>
              </div>
            ) : (
              messages.map((message, index) => (
                <div 
                  key={index} 
                  className={`p-4 rounded-xl max-w-[75%] shadow-lg relative transition-all duration-300 ease-in-out transform 
                    ${message.role === 'user' 
                      ? 'bg-blue-700 text-white ml-auto rounded-br-none hover:scale-105' 
                      : 'bg-gray-700 text-gray-100 mr-auto rounded-bl-none hover:scale-105'}`}
                >
                  <p className="font-semibold text-sm opacity-80 mb-1">{message.role === 'user' ? 'You:' : 'AI:'}</p>
                  <p className="text-base leading-relaxed">{message.content}</p>
                </div>
              ))
            )
          ) : (
            <div className="flex items-center justify-center h-full text-gray-500 text-center text-xl p-4 animate-fade-in">
              <div>
                <h3 className="text-3xl mb-3 text-gray-400">👋 Welcome to Inquiero!</h3>
                <p className="text-lg">Upload a PDF to start chatting or select a conversation from the sidebar.</p>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Message Input Form */}
        <form onSubmit={handleSendMessage} className="flex items-center space-x-4">
          <input
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="Ask a question about the PDF..."
            className="flex-1 p-3 rounded-full bg-gray-700 text-gray-100 border border-gray-600 focus:outline-none focus:border-blue-500 transition duration-200 ease-in-out text-lg pl-5 pr-5 shadow-inner"
            disabled={!currentChatId || loading}
          />
          <button 
            type="submit" 
            className="bg-blue-600 hover:bg-blue-700 text-white rounded-full p-4 flex items-center justify-center transition duration-300 ease-in-out transform hover:scale-110 shadow-lg"
            disabled={!currentChatId || loading}
          >
            <span className="text-2xl">🚀</span>
          </button>
        </form>
      </main>

      {/* PDF Preview Modal */}
      {showPdfModal && pdfUrlToPreview && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50 animate-fade-in">
          <div className="bg-gray-800 rounded-lg shadow-xl p-6 w-11/12 max-w-4xl max-h-[90vh] overflow-hidden flex flex-col animate-zoom-in">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-2xl font-bold text-white">PDF Preview</h2>
              <button 
                className="text-gray-400 hover:text-gray-100 text-3xl transition-colors duration-200"
                onClick={() => setShowPdfModal(false)}
              >
                &times;
              </button>
            </div>
            <div className="flex-grow overflow-auto bg-gray-900 rounded-md p-2">
              <Document
                file={pdfUrlToPreview}
                onLoadSuccess={onDocumentLoadSuccess}
                loading="Loading PDF..."
                error="Failed to load PDF."
              >
                {Array.from(new Array(numPages), (el, index) => (
                  <Page
                    key={`page_${index + 1}`}
                    pageNumber={index + 1}
                    renderTextLayer={false}
                    renderAnnotationLayer={false}
                    className="mb-4"
                  />
                ))}
              </Document>
            </div>
            {numPages && (
              <div className="flex justify-center items-center mt-4 text-white">
                <button 
                  className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-1 px-3 rounded-md mr-2 disabled:opacity-50"
                  disabled={pageNumber <= 1}
                  onClick={() => setPageNumber(prevPageNumber => prevPageNumber - 1)}
                >
                  Previous
                </button>
                <span>Page {pageNumber} of {numPages}</span>
                <button 
                  className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-1 px-3 rounded-md ml-2 disabled:opacity-50"
                  disabled={pageNumber >= numPages}
                  onClick={() => setPageNumber(prevPageNumber => prevPageNumber + 1)}
                >
                  Next
                </button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
